import java.io.FileInputStream;
import java.io.IOException;
import java.util.concurrent.TimeUnit;

import org.apache.poi.ss.usermodel.Cell;
import org.apache.poi.ss.usermodel.Row;
import org.apache.poi.ss.usermodel.Sheet;
import org.apache.poi.ss.usermodel.Workbook;
import org.apache.poi.ss.usermodel.WorkbookFactory;
import org.openqa.selenium.By;
import org.openqa.selenium.Keys;
import org.openqa.selenium.WebDriver;
import org.openqa.selenium.WebElement;
import org.openqa.selenium.chrome.ChromeDriver;
import org.openqa.selenium.interactions.Actions;
import org.testng.annotations.AfterTest;
import org.testng.annotations.BeforeTest;
import org.testng.annotations.Test;

public class Freecrm {

    WebDriver driver;
    String filePath = "C:\\path\\to\\FreeCRMContacts.xlsx";
    String[] data = new String[22]; // Adjusted size to match the number of data points

    @BeforeTest
    public void open_browser() throws IOException {
        System.setProperty("webdriver.chrome.driver", "C:\\Driver\\chromedriver.exe");
        driver = new ChromeDriver();
        driver.manage().window().maximize();
        driver.manage().timeouts().implicitlyWait(10, TimeUnit.SECONDS);
        driver.get("https://ui.cogmento.com");

        // Read data from Excel file
        FileInputStream fis = new FileInputStream("C:\\Users\\PKARANSI\\OneDrive - Capgemini\\Notebooks\\FreeCRMContacts.xlsx");
        Workbook workbook = WorkbookFactory.create(fis);
        Sheet sheet = workbook.getSheetAt(0);
        int i = 0;
        for (Row row : sheet) {
            for (Cell cell : row) {
                data[i] = cell.getStringCellValue();
                System.out.println("Data[" + i + "]: " + data[i]); // Debugging statement
                i++;
            }
        }
        workbook.close(); // Close the workbook here
        fis.close();
    }

    @Test(priority = 2)
    public void Crm_Contact_LOGIN() {
        // Login details
        WebElement login = driver.findElement(By.xpath("//*[@id=\"ui\"]/div/div/form/div/div[1]/div/input"));
        login.sendKeys(data[2]); // Email
        WebElement pass = driver.findElement(By.xpath("//*[@id=\"ui\"]/div/div/form/div/div[2]/div/input"));
        pass.sendKeys(data[3]); // Password
        WebElement enter = driver.findElement(By.xpath("//*[@id=\"ui\"]/div/div/form/div/div[3]"));
        enter.click();
    }

    @Test(priority = 3)
    public void open_company_form_OPEN() {
        WebElement contact = driver.findElement(By.xpath("//*[@id=\"main-nav\"]/div[3]/button"));
        Actions act = new Actions(driver);
        act.moveToElement(contact).click().keyDown(contact, Keys.SHIFT)
            .keyUp(contact, Keys.SHIFT).doubleClick(contact).moveByOffset(100, 100).build().perform();

        // Personal Details
        WebElement Fname = driver.findElement(By.name("first_name"));
        Fname.sendKeys(data[4]); // First Name
        WebElement Lname = driver.findElement(By.name("last_name"));
        Lname.sendKeys(data[5]); // Last Name
        WebElement Mname = driver.findElement(By.name("middle_name"));
        Mname.sendKeys(data[6]); // Middle Name
        WebElement Company = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[2]/div[2]/div/div/input"));
        Company.sendKeys(data[7]); // Company

        // Click Method
        WebElement pvt = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[3]/div[1]/div/div/div[1]/button"));
        pvt.click();
        WebElement clk1 = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[3]/div[1]/div/div/div[2]/div"));
        clk1.click();
        WebElement clk2 = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[3]/div[1]/div/div/div[2]/div/div[2]/div"));
        clk2.click();

        // Tag
        WebElement tag = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[3]/div[2]/div/div/input"));
        tag.sendKeys(data[8]); // Tag
        WebElement clk3 = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[3]/div[2]/div/div/div/div"));
        clk3.click();

        // Company Email
        WebElement email = driver.findElement(By.name("value"));
        email.sendKeys(data[9]); // Company Email
    }

    @Test(priority = 4)
    public void open_company_form_ZIP() {
        // Select Options
        WebElement category = driver.findElement(By.name("category"));
        category.click();
        WebElement lead = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[4]/div[2]/div/div/div[2]/div[2]"));
        lead.click();
        WebElement status = driver.findElement(By.name("status"));
        status.click();
        WebElement Active = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[5]/div[1]/div/div/div[2]/div[3]"));
        Active.click();

        // LinkedIn
        WebElement channel_type = driver.findElement(By.name("channel_type"));
        channel_type.click();
        WebElement LinkedIn = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[6]/div[1]/div/div/div/div[1]/div/div[2]/div[3]"));
        LinkedIn.click();
        WebElement LinkedInFill = driver.findElement(By.xpath("//*[@id=\"main-content\"]/div/div[2]/form/div[6]/div[1]/div/div/div/div[2]/div/input"));
        LinkedInFill.sendKeys(data[17]); // LinkedIn URL

        // Address
        WebElement address = driver.findElement(By.name("address"));
        address.sendKeys(data[18]); // Address
        driver.findElement(By.name("state")).sendKeys(data[19]); // State
        driver.findElement(By.name("city")).sendKeys(data[20]); // City
        WebElement zip = driver.findElement(By.name("zip"));
        zip.sendKeys(data[21]); // Zip
    }

    @Test(priority = 5)
    public void open_company_form_NUMBER() {
        // Phone number and Details
        WebElement Number = driver.findElement(By.xpath("//input[@placeholder='Number']"));
        Number.sendKeys(data[10]); // Phone Number
        driver.findElement(By.name("position")).sendKeys(data[11]); // Position
        driver.findElement(By.name("department")).sendKeys(data[12]); // Department
    }

    @Test(priority = 6)
    public void open_company_form_SAVE() {
        // Save Button Click
        WebElement save = driver.findElement(By.xpath("//div[@id='dashboard-toolbar']/div[2]/div/button[2]/i"));
        save.click();
    }

    @AfterTest
    public void close_browser() {
        driver.close();
    }
}

Prompt Level: 12-Upper Intermediate [SEP] Prompt: Your friend Kim needs help with emails. Read the beginning of the email. Then, make a list of tips for writing effective emails. Type in the input box. Write 70-90 words. Use your own words where possible. [SEP] Response: - start with a clear and specific subject line;
- use shorts paragrahs and bullet point if necessary;
- use a polite and professional tone avoiding jokes;
- keep the message concise;
- include a clear call to action if needed;
- use emoticons only in informal emails;
- don't forget to add a proper greeting and closing;
- make sure your emails is addressed to the right person or people;
- be careful to avoid selecting "reply all" if you don't want to share your message with other people.

