Prompt Level: 13.0 [SEP] Prompt: Use the information in the notes to write an informative text about NAFTA, or write about a trade agreement your country is a part of. Type in the input box. Write 150-200 words. [SEP] Response: -- Procedure to Get All Employees Under a Manager
CREATE OR REPLACE PROCEDURE GetWorkersUnderManager (p_manager_id IN NUMBER) AS
BEGIN
   FOR worker IN (
      SELECT e.Employee_Number, e.Name
      FROM Emp_Master e
      WHERE e.Employee_Number NOT IN (SELECT Employee_Number FROM Manager_Master)
      AND e.Employee_Number IN (SELECT Employee_Number FROM Emp_Master)
   ) LOOP
      DBMS_OUTPUT.PUT_LINE('Worker ID: ' || worker.Employee_Number || ', Name: ' || worker.Name);
   END LOOP;
END;
/

-- Procedure to Get List of All Current Backorders by Manager
CREATE OR REPLACE PROCEDURE GetBackordersByManager (p_manager_id IN NUMBER) AS
BEGIN
    FOR rec IN (
        SELECT * 
        FROM Current_Backorder
        WHERE Employee_Number IN (SELECT Employee_Number FROM Manager_Master WHERE Employee_Number = p_manager_id)
    ) LOOP
        DBMS_OUTPUT.PUT_LINE('Backorder ID: ' || rec.Backorder_Id || ', Part Number: ' || rec.Part_Number || ', Remaining Quantity: ' || rec.Remaining_Quantity);
    END LOOP;
END;
/

-- Procedure to Get Remaining Capacity of Each Warehouse Bin
CREATE OR REPLACE PROCEDURE GetRemainingCapacity (p_bin_number IN NUMBER) AS
BEGIN
    FOR rec IN (
        SELECT Bin_Number, Capacity - SUM(Item_Master.Quantity) AS Remaining_Capacity
        FROM Bin_Details
        LEFT JOIN Item_Master
        ON Bin_Details.Warehouse_Id = Item_Master.Warehouse_Id
        GROUP BY Bin_Details.Bin_Number, Bin_Details.Capacity
        HAVING Bin_Details.Bin_Number = p_bin_number
    ) LOOP
        DBMS_OUTPUT.PUT_LINE('Bin Number: ' || rec.Bin_Number || ', Remaining Capacity: ' || rec.Remaining_Capacity);
    END LOOP;
END;
/

-- Procedure to Get Manager with the Smallest Number of Workers Managed
CREATE OR REPLACE PROCEDURE GetManagerWithFewestWorkers AS
BEGIN
    FOR rec IN (
        SELECT m.Employee_Number, m.Name, COUNT(e.Employee_Number) AS Worker_Count
        FROM Manager_Master m
        LEFT JOIN Emp_Master e
        ON m.Employee_Number = e.Manager_Id
        GROUP BY m.Employee_Number, m.Name
        ORDER BY Worker_Count ASC
        FETCH FIRST 1 ROWS ONLY
    ) LOOP
        DBMS_OUTPUT.PUT_LINE('Manager ID: ' || rec.Employee_Number || ', Name: ' || rec.Name || ', Workers Managed: ' || rec.Worker_Count);
    END LOOP;
END;
/

-- Procedure to Get Phones and Employee Numbers for All Managers
CREATE OR REPLACE PROCEDURE GetPhonesForManagers AS
BEGIN
    FOR rec IN (
        SELECT e.Employee_Number, p.Phone_Number
        FROM Emp_Master e
        JOIN Emp_Phone p ON e.Employee_Number = p.Employee_Number
        WHERE e.Employee_Number IN (SELECT Employee_Number FROM Manager_Master)
    ) LOOP
        DBMS_OUTPUT.PUT_LINE('Manager Employee No: ' || rec.Employee_Number || ', Phone Number: ' || rec.Phone_Number);
    END LOOP;
END;
/

-- Procedure to Update Backorder Status When Shipment Arrives
CREATE OR REPLACE PROCEDURE UpdateBackorderStatusOnArrival (
    p_backorder_id IN NUMBER,
    p_shipped_quantity IN NUMBER
) AS
BEGIN
    DECLARE
        v_remaining_quantity NUMBER;
    BEGIN
        -- Get current remaining quantity
        SELECT Remaining_Quantity INTO v_remaining_quantity
        FROM Current_Backorder
        WHERE Backorder_Id = p_backorder_id;

        -- Subtract the shipped quantity
        v_remaining_quantity := v_remaining_quantity - p_shipped_quantity;

        -- Update the backorder remaining quantity
        UPDATE Current_Backorder
        SET Remaining_Quantity = v_remaining_quantity
        WHERE Backorder_Id = p_backorder_id;

        -- If remaining quantity is 0 or less, delete the backorder
        IF v_remaining_quantity <= 0 THEN
            DELETE FROM Current_Backorder WHERE Backorder_Id = p_backorder_id;
            DBMS_OUTPUT.PUT_LINE('Backorder ID: ' || p_backorder_id || ' deleted due to zero or negative remaining quantity');
        ELSE
            DBMS_OUTPUT.PUT_LINE('Backorder ID: ' || p_backorder_id || ' updated with remaining quantity: ' || v_remaining_quantity);
        END IF;
    END;
END;
/

-- Trigger to Log Manager's Backorder Arrival
CREATE OR REPLACE TRIGGER LogManagerBackorderArrival
AFTER INSERT ON Item_Master
FOR EACH ROW
BEGIN
    INSERT INTO Backorder_Log (Log_Id, Backorder_Id, Action, Action_Date, Employee_Number)
    VALUES (Backorder_Log_Seq.NEXTVAL, :NEW.Backorder_Id, 'Backorder Arrival Processed', SYSDATE, :NEW.Checked_Out_By);
    DBMS_OUTPUT.PUT_LINE('Manager action logged for Backorder ID: ' || :NEW.Backorder_Id);
END;
/

